import React, { Component } from 'react';
import Header from './Header';
import Footer from './Footer';
import ImageCaraousel from './ImageCarousel';

class Main extends Component
{
    constructor()
    {
       
        this.state={
            pics:[{imageLink:'https://i1.sndcdn.com/artworks-000482183808-s7h0nx-t500x500.jpg'},
                  {imageLink:'https://i.ytimg.com/vi/8qLL2Gx3I_k/maxresdefault.jpg'},
                  {imageLink:'https://i.ytimg.com/vi/9bXgeAJbM1g/maxresdefault.jpg'}
        ],
        i:0
        }
        this.i=0;
        this.onNext = this.onNext.bind(this);
        this.onPrevious = this.bind(this);

    }
    render()
    {
        return (
            <div>
                <Header></Header>
                <ImageCaraousel></ImageCaraousel>
                <Footer></Footer>

            </div>
        );
    }

    onPrevious()
    {
        console.log('previous');
        this.setState((state)=>({
            i:(i>=0?state.i-1:0)
        }))
        
    }
    onNext()
    {
        console.log('next');
        if((this.i)>=2)
        {
            (this.i)=0;
            document.getElementById("carouselItem").src=this.ar[(this.i)];
        }
        else{
            document.getElementById("carouselItem").src=this.ar[++(this.i)];
            
        }
    }

}

export default Main;
