import React ,{Component} from 'react';

class ImageCaraousel extends Component
{
    constructor()
    {
        super();
        const url1='https://i1.sndcdn.com/artworks-000482183808-s7h0nx-t500x500.jpg';
        const url2='https://(this.i).ytimg.com/vi/8qLL2Gx3I_k/maxresdefault.jpg';
        const url3='https://(this.i).ytimg.com/vi/9bXgeAJbM1g/maxresdefault.jpg';
        this.ar=[url1,url2,url3];
        this.i=0;
    }


    render()
    {
        return (
            <div class="carousel" >
            <div>
                <button onclick="onPrevious()" class="carousel-previous-btn"> Previous</button>
                <img src="https://i1.sndcdn.com/artworks-000482183808-s7h0nx-t500x500.jpg" id="carouselItem"/>
                <button onclick="onNext()" class="carousel-next-btn"> Next</button>
    
            </div>
        </div>
                );
    }
    onPrevious()
    {
        if((this.i)<=0)
        {
            (this.i)=2;
            document.getElementById("carouselItem").src=this.ar[(this.i)];
        }
        else{
            document.getElementById("carouselItem").src=this.ar[--(this.i)];
        }
    }
    onNext()
    {
        if((this.i)>=2)
        {
            (this.i)=0;
            document.getElementById("carouselItem").src=this.ar[(this.i)];
        }
        else{
            document.getElementById("carouselItem").src=this.ar[++(this.i)];
            
        }
    }
}

export default ImageCaraousel;
